) R

	rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE tb_member
SET member_name = #{member_name }, nick = #{nick }, member_addr = #{member_addr }, member_grade = #{member_grade }, point = #{point }
WHERE member_id = #{member_id }

UPDATE tb_show
SET show_division = #{show_division}
	, show_genre = #{show_genre}
	, show_place = #{show_place}
	, show_name = #{show_name}


	, show_rprice = #{show_rprice}
	, show_sprice = #{show_sprice}
	, show_time = #{show_time}
	, show_lat = #{show_lat}
	, show_len = #{show_len}
	, show_call = #{show_call}
	, show_image = #{show_image}
	, show_cast = #{show_cast}
	, show_age = #{show_age}
	, show_host = #{show_host}
	, show_sub = #{show_sub}
	, show_addr = #{show_addr}
WHERE show_id = #{show_id }

mapper.insert:
INSERT INTO tb_show (
	show_id
	, show_division
	, show_genre
	, show_place
	, show_name
	, show_start
	, show_end
	, show_rprice
	, show_sprice
	, show_time
	, show_lat
	, show_len
	, show_call
	, show_image
	, show_cast
	, show_age
	, show_host
	, show_sub
	, show_addr
	)
VALUES (
	#{show_id}
	, #{show_division}
	, #{show_genre}
	, #{show_place}
	, #{show_name}
	, TO_CHAR(#{show_start},'RR/MM/DD')
	, TO_CHAR(#{show_end},'RR/MM/DD')
	, #{show_rprice}
	, #{show_sprice}
	, #{show_time}
	, #{show_lat}
	, #{show_len}
	, #{show_call}
	, #{show_image}
	, #{show_cast}
	, #{show_age}
	, #{show_host}
	, #{show_sub}
	, #{show_addr})

mapper.select:
SELECT to_char(payment_date, 'RR/MM') payment_date ,SUM(decode(seat_grade,'S',1,0)) S_SEAT_C ,SUM(decode(seat_grade,'R',1,0)) R_SEAT_C ,SUM(decode(seat_grade,'S',1,0))+sum(decode(seat_grade,'R',1,0)) TOTAL_C ,SUM(decode(seat_grade,'S',seat_price,0)) S_SEAT_P ,SUM(decode(seat_grade,'R',seat_price,0)) R_SEAT_P ,SUM(decode(seat_grade,'S',seat_price,0))+sum(decode(seat_grade,'R',seat_price,0)) TOTAL_P FROM tb_book WHERE status = 'Y'
AND TO_CHAR(payment_date, 'YYYYMM') = ${book_year}${book_month}
AND TO_CHAR(payment_date, 'YYYY') = ${book_year}
GROUP BY to_char(payment_date, 'RR/MM')

